//PROGRAM TO ILLUSTRATE THE WORKING OF DO WHILE LOOP

package java_programs;

public class Do_whiledemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 1; 
		 
		 do { 
		 System.out.print("value of x : " + x ); 
		 x++; 
		 System.out.print("\n"); 
		 }while( x < 11 );
	}

}
